import React, {useState} from 'react';
import styled from 'styled-components'
// import logo from './logo.svg';
import './App.css';

import MusicUI from 'sequencer'
import Khord from 'chord-generator'

const { chordGenerator, patternMaker } = Khord

const {
  playWorkbench,
  stopWorkbench
} = patternMaker

const {
  getRandomProgression
} = chordGenerator;

const {
  ProgressionChordOptions,
  ProgressionWorkbench,
  ProgressionPatternOptions
} = MusicUI

const Workbench = styled.div`
  /* border: solid; */
  width: 100%;
`
const Options = styled.div`
  border-left: solid;
  width: 100%;
  height: 100%;
`

const Wrapper = styled.div`
  border: solid;
  display: flex;
  height: 25vh;
`

function App() {

  const [chords, setChords] = useState([])
  const [key, setKey] = useState('D')
  const [mode, setMode] = useState('mixolydian')
  const [chordPattern, setChordPattern] = useState([])

  console.log(getRandomProgression({
    key,
    mode,
    theme: null
  }
  ))
  return (
    <div className="App">
      <Wrapper>

        <Workbench>
          <ProgressionWorkbench
            chords={chords.chordNames}
            play={() => playWorkbench({
              sections: [chords],
              stateUpdater: () => {console.log('update')},
              params: {pattern: [
                [
                  {  time: '0:0:0', duration: '0:0:0'},
                  {  time: '0:1:0', duration: '0:2:0'},
                  {  time: '0:2:0', duration: '0:2:2'},
                  {  time: '0:3:0', duration: '0:3:0'}
                ],
                [
                  {  time: '1:0:0', duration: '8n'},
                  {  time: '1:1:0', duration: '8n'},
                  {  time: '1:2:0', duration: '8n'},
                  {  time: '1:3:0', duration: '8n'}
                ],
                [
                  {  time: '2:0:0', duration: '8n'},
                  {  time: '2:1:0', duration: '8n'},
                  {  time: '2:2:0', duration: '8n'},
                  {  time: '2:3:0', duration: '8n'}
                ],[
                  {  time: '3:0:0', duration: '8n'},
                  {  time: '3:1:0', duration: '8n'},
                  {  time: '3:2:0', duration: '8n'},
                  {  time: '3:3:0', duration: '8n'}
                ]

              ]}
            })}
            stop={stopWorkbench}
          />
        </Workbench>

        <Options>
          <ProgressionPatternOptions
            onChange={(pattern) => {setChordPattern(pattern)}}
          />
          <ProgressionChordOptions
            onSubmit={
              () => setChords(getRandomProgression(
                { key, mode, theme: null }
              ))}
            onKeyChange={ ({target: {value}}) => setKey(value) }
            onModeChange={ ({target: {value}}) => setMode(value)}
          />
        </Options>

      </Wrapper>

    </div>
  );
}

export default App;
