import React, {useState} from 'react';
import styled from 'styled-components'
// import logo from './logo.svg';
import './App.css';

import MusicUI from 'sequencer'
import Khord from 'chord-generator'

const { chordGenerator, patternMaker } = Khord

const {
  playWorkbench,
  stopWorkbench,
} = patternMaker

const {
  getRandomProgression,
  getAllChords
} = chordGenerator;

const {
  ProgressionChordOptions,
  ProgressionWorkbench,
  ProgressionPatternOptions
} = MusicUI

const Workbench = styled.div`
  /* border: solid; */
  width: 100%;
`
const Options = styled.div`
  border-left: solid;
  width: 100%;
  height: 100%;
`

const Wrapper = styled.div`
  border: solid;
  display: flex;
  height: 25vh;
`

function App() {

  const [chords, setChords] = useState([])
  const [key, setKey] = useState('D')
  const [mode, setMode] = useState('mixolydian')
  const [measureCount, setMeasureCount] = useState('1')
  const [chordPattern, setChordPattern] = useState([])

  console.log(chords)

  return (
    <div className="App">
      <Wrapper>

        <Workbench>
          <ProgressionWorkbench
            getKeyChords={() => getAllChords({mode, key})}
            changeChord={({name, notes, index}) => {
              const newChords = {...chords}
              newChords.chordNames[index] = name
              newChords.chordNotes[index] = notes
              setChords(newChords)
            }}
            chords={chords.chordNames}
            play={() => playWorkbench({
              sections: [chords],
              stateUpdater: () => {console.log('update')},
              params: { pattern: chordPattern, measureCount}
            })}
            stop={stopWorkbench}
          />
        </Workbench>

        <Options>
          <ProgressionPatternOptions
            onChange={(pattern) => {setChordPattern(pattern)}}
          />
          <ProgressionChordOptions
            onSubmit={
              () => setChords(getRandomProgression(
                { key, mode, measureCount, theme: null }
              ))}
            onKeyChange={ ({target: {value}}) => setKey(value) }
            onModeChange={ ({target: {value}}) => setMode(value)}
            onMeasureCountChange={ ({target: {value}}) => setMeasureCount(value)}
          />
        </Options>

      </Wrapper>

    </div>
  );
}

export default App;
