import React, {useState} from 'react';
import styled from 'styled-components'
// import logo from './logo.svg';
import './App.css';

import MusicUI from 'sequencer'

const {
  ProgressionChordOptions,
  ProgressionWorkbench,
  ProgressionPatternOptions
} = MusicUI

const Workbench = styled.div`
  /* border: solid; */
  width: 100%;
`
const Options = styled.div`
  border-left: solid;
  width: 100%;
  height: 100%;
`

const Wrapper = styled.div`
  border: solid;
  display: flex;
  height: 25vh;
`

function App() {

  const [chords, setChords] = useState([])
  const [key, setKey] = useState('a')
  const [mode, setMode] = useState('lydian')


  return (
    <div className="App">
      <Wrapper>

        <Workbench>
          <ProgressionWorkbench chords={chords} />
        </Workbench>

        <Options>
          <ProgressionPatternOptions/>
          <ProgressionChordOptions
            onSubmit={ () => setChords([{}]) }
            onKeyChange={ () => setKey('e') }
            onModeChange={ () => setMode('locrian')}
          />
        </Options>

      </Wrapper>

    </div>
  );
}

export default App;
