import React, {useState} from 'react';
// import logo from './logo.svg';
import './App.css';

import textExploder from 'text-exploder-two'

const { Documents, dummyDocumentStore, Document, dummyDocumentList } = textExploder


function App() {

  const [currDocID, setCurrDocID] = useState(false)

  console.log('=> ', currDocID)

  return (
    <div className="App">
      {
        currDocID ?
          <Document data={dummyDocumentStore[currDocID]} back={() => setCurrDocID(false)} /> :
          <Documents data={dummyDocumentList} onSelect={(id) => {setCurrDocID(id)}}/>
      }
    </div>
  );
}

export default App;
